[Form]
public class ADMConfiguration extends FormRun
{
    ADMConfigs config;

}

public void init()
{
    super();

    int reportOffset = 30;
    int reasonDialog = 0;
    str tenantId = "";
    str clientId = "";
    str clientSecret = "";

    select firstonly Value from config where config.Name == "DefaultNumDaysForReports";
    if(config)
    {
        if(str2NumOK(config.Value))
        {
            reportOffset = str2Num(config.Value);
        }
    }

    select firstonly Value from config where config.Name == "ShowReasonDialog";
    if(config)
    {
        if(str2NumOK(config.Value))
        {
            reasonDialog = str2Num(config.Value);
        }
    }

    select firstonly Value from config where config.Name == "EntTenantId";
    if(config)
    {
        tenantId = config.Value;
    }

    select firstonly Value from config where config.Name == "EntClientId";
    if(config)
    {
        clientId = config.Value;
    }

    select firstonly Value from config where config.Name == "EntClientSecret";
    if(config)
    {
        clientSecret = config.Value;
    }

    ent_tenantid.text(tenantId);
    ent_clientid.text(clientId);
    ent_clientsecret.text(clientSecret);

    ReportNumDays.value(reportOffset);
    ShowReasonDialog.value(reasonDialog);
}

public void clicked()
{
    select forupdate config where config.Name == "DefaultNumDaysForReports";
    if(config)
    {
        str reportOffset = ReportNumDays.valueStr();
        ttsbegin;
        config.Value = reportOffset;
        config.update();
        ttscommit;
    }
    else
    {
        config.clear();
        config.Name = "DefaultNumDaysForReports";
        str reportOffset = ReportNumDays.valueStr();
        ttsbegin;
        config.Value = reportOffset;
        config.insert();
        ttscommit;
    }

    select forupdate config where config.Name == "ShowReasonDialog";
    if(config)
    {
        int reasonDialog = ShowReasonDialog.value();
        ttsbegin;
        if(reasonDialog == 1)
        {
            config.Value = "1";
        }
        else
        {
            config.Value = "0";
        }

        config.update();
        ttscommit;
    }
    else
    {
        config.clear();
        config.Name = "ShowReasonDialog";
        int reasonDialog = ShowReasonDialog.value();
        ttsbegin;
        if(reasonDialog == 1)
        {
            config.Value = "1";
        }
        else
        {
            config.Value = "0";
        }
        config.insert();
        ttscommit;
    }

    select forupdate config where config.Name == "EntTenantId";
    if(config)
    {
        str tenantId = ent_tenantid.valueStr();
        ttsbegin;
        config.Value = tenantId;
        config.update();
        ttscommit;
    }
    else
    {
        config.clear();
        config.Name = "EntTenantId";
        str tenantId = ent_tenantid.valueStr();
        ttsbegin;
        config.Value = tenantId;
        config.insert();
        ttscommit;
    }

    select forupdate config where config.Name == "EntClientId";
    if(config)
    {
        str clientId = ent_clientId.valueStr();
        ttsbegin;
        config.Value = clientId;
        config.update();
        ttscommit;
    }
    else
    {
        config.clear();
        config.Name = "EntClientId";
        str clientId = ent_clientId.valueStr();
        ttsbegin;
        config.Value = clientId;
        config.insert();
        ttscommit;
    }

    select forupdate config where config.Name == "EntClientSecret";
    if(config)
    {
        str clientSecret = ent_clientSecret.valueStr();
        ttsbegin;
        config.Value = clientSecret;
        config.update();
        ttscommit;
    }
    else
    {
        config.clear();
        config.Name = "EntClientSecret";
        str clientSecret = ent_clientSecret.valueStr();
        ttsbegin;
        config.Value = clientSecret;
        config.insert();
        ttscommit;
    }

    Box::info("D365FO Admin Toolkit Configuration Saved Successfully", "", "");

    element.close();
    super();
}
